import React, { Component } from 'react';
import { View, Text, Button } from 'react-native';
import RNLocation from 'react-native-location';

export default class Loc extends Component {
  permissionHandle = async () => {
    console.log('here');

    let permission = await RNLocation.checkPermission({
      ios: 'whenInUse', // or 'always'
      android: {
        detail: 'coarse', // or 'fine'
      },
    });

    console.log('here2');
    console.log(permission);

    if (permission === false) {
      this.permission();
    } else {
      this.getLocation();
    }
  };

  permission = async () => {
    await RNLocation.requestPermission({
      ios: 'whenInUse',
      android: {
        detail: 'coarse',
        rationale: {
          title: 'We need to access your location',
          message: 'We use your location to show where you are on the map',
          buttonPositive: 'OK',
          buttonNegative: 'Cancel',
        },
      },
    });

    let permission = await RNLocation.checkPermission({
      ios: 'whenInUse', // or 'always'
      android: {
        detail: 'coarse', // or 'fine'
      },
    });
    console.log(permission);
  };

  getLocation = async () => {
    console.log('getLoc started');

    RNLocation.configure({
      distanceFilter: null,
    });

    console.log("distance filtered");

    let location;

    location = await RNLocation.subscribeToLocationUpdates((locations) => {});
    console.log(
      location,
      location.longitude,
      location.latitude,
      location.timestamp
    );
    console.log('getLOC done');
  };

  render() {
    return (
      <View>
        <View
          style={{
            marginTop: 10,
            padding: 10,
            borderRadius: 10,
            width: '40%',
          }}>
          <Button title="Get Location" onPress={this.permissionHandle} />
        </View>
        <View>
          <Text>"Latitude: "</Text>
          <Text>"Longitude: "</Text>
        </View>
        <View
          style={{
            marginTop: 10,
            padding: 10,
            borderRadius: 10,
            width: '40%',
          }}>
          <Button title="Send Location" />
        </View>
      </View>
    );
  }
}
